import React from 'react';
import { Link, animateScroll as scroll } from 'react-scroll';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import './Sidedrawer.styles.css';

const SideDrawer = ({ show }) => {
  let drawerClasses = 'side-drawer';
  if (show) {
    drawerClasses = 'side-drawer open';
  }

  return (
    <nav className={drawerClasses}>
      <div className="navbar-toggle-button">
        {/* <DrawerToggleButton click={drawerClickHander} /> */}
      </div>
      <div className="navbar-logo">
        <Link
          activeClass="active"
          to="hero"
          spy={true}
          smooth={true}
          offset={-70}
          duration={500}
        >
          JK
        </Link>
      </div>
      <ul>
        <li>
          <Link
            activeClass="active"
            to="about"
            spy={true}
            smooth={true}
            offset={-70}
            duration={500}
          >
            <FontAwesomeIcon icon="user-astronaut" className="option-icon" />
            About
          </Link>
        </li>
        <li>
          <Link
            activeClass="active"
            to="project-section"
            spy={true}
            smooth={true}
            offset={-70}
            duration={500}
          >
            <FontAwesomeIcon icon="wrench" className="option-icon" />
            Projects
          </Link>
        </li>
        <li>
          <Link
            activeClass="active"
            to="contact"
            spy={true}
            smooth={true}
            offset={-70}
            duration={500}
          >
            <FontAwesomeIcon icon="paper-plane" className="option-icon" />
            Contact Me
          </Link>
        </li>
      </ul>
    </nav>
  );
};

export default SideDrawer;
