import React from 'react';
import { Route } from 'react-router-dom';
import './App.css';
import Main from './pages/Main';
import Header from './components/header/Header';

const App = () => {
  const [isAppearing, setIsAppearing] = React.useState(false);
  const [top, setTop] = React.useState(null);
  const elementToObserveRef = React.useRef();

  React.useEffect(() => {
    const observer = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting) {
        console.log('intersecting');
        setIsAppearing(true);
      } else {
        console.log('NOT intersecting');
        setIsAppearing(false);
      }
    });
    observer.observe(elementToObserveRef);
    return () => observer.disconnect(); // cleanup
  }, []);

  React.useEffect(() => {
    if (isAppearing) {
      const listener = () => {
        const elementRect = elementToObserveRef.current.getBoundingClientRect();
        setTop(elementRect.top);
      };
      window.addEventListener('scroll', listener);
      return () => window.removeEventListener('scroll', listener);
    }
  }, [isAppearing]);

  return (
    <div>
      <Header />
      <Main />
      <div className="section1" ref={elementToObserveRef}></div>
      {/* <Route exact path="/" component={Main} /> */}
    </div>
  );
};

export default App;
