import React from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

// import { ReactComponent as Logo } from '../../assets/logo.png';
import './header.styles.scss';

const Header = () => {
  const [isAppearing, setIsAppearing] = React.useState(false);
  const [top, setTop] = React.useState(null);
  const elementToObserveRef = React.useRef();

  React.useEffect(() => {
    const observer = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting) {
        console.log('intersecting');
        setIsAppearing(true);
      } else {
        console.log('NOT intersecting');
        setIsAppearing(false);
      }
    });
    observer.observe(elementToObserveRef.current);
    return () => observer.disconnect(); // cleanup
  }, []);

  React.useEffect(() => {
    if (isAppearing) {
      const listener = () => {
        const elementRect = elementToObserveRef.current.getBoundingClientRect();
        setTop(elementRect.top);
      };
      window.addEventListener('scroll', listener);
      return () => window.removeEventListener('scroll', listener);
    }
  }, [isAppearing]);

  return (
    <div className="header">
      <Link className="logo-container" to="/">
        JK
      </Link>
      <div className="options">
        <Link className="option" to="/#about">
          <FontAwesomeIcon icon="user-astronaut" className="option-icon" />
          ABOUT
        </Link>
        <Link className="option" to="/#project-section">
          <FontAwesomeIcon icon="wrench" className="option-icon" />
          PROJECTS
        </Link>

        <Link className="option" to="/#contact">
          <FontAwesomeIcon icon="paper-plane" className="option-icon" />
          CONTACT
        </Link>

        <div className="option-separator">|</div>
        <a href className="nav-icon" to="">
          <FontAwesomeIcon icon={['fab', 'github']} />
        </a>
        <a href className="nav-icon" to="">
          <FontAwesomeIcon icon={['fab', 'linkedin']} />
        </a>
      </div>
    </div>
  );
};
export default Header;
