import React from 'react';
import './Side-drawer.styles.css';

const SideDrawer = ({ show }) => {
  let drawerClasses = 'side-drawer';
  if (show) {
    drawerClasses = 'side-drawer open';
  }

  return (
    <nav className={drawerClasses}>
      <ul>
        <li>
          <a href="/">About</a>
        </li>
        <li>
          <a href="/">Contact</a>
        </li>
        <li>
          <a href="/">More</a>
        </li>
      </ul>
    </nav>
  );
};

export default SideDrawer;
