import React from 'react';
import { Link, animateScroll as scroll } from 'react-scroll';

import './custom-button.styles.scss';

const CustomButton = ({ children, buttonOrLink, to, ...otherProps }) => {
  let { buttonType } = props;
  switch (buttonType) {
    case 'anchor':
      return <button>Logout</button>;
      break;
    case 'external':
      return <button>Login</button>;
      break;
    case 'submit':
      return <button>Login</button>;
      break;
    default:
      return null;
  }

  return (
    <div>
      {buttonOrLink === 'button' ? (
        <button
          className="custom-button fancy-button bg-gradient1"
          {...otherProps}
        >
          {children}
        </button>
      ) : (
        <Link
          activeClass="active"
          to={to}
          spy={true}
          smooth={true}
          offset={-70}
          duration={500}
          className="custom-button fancy-button bg-gradient1"
          {...otherProps}
        >
          {children}
        </Link>
      )}
    </div>
  );
};

export default CustomButton;
